import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { getAllNotebooks, createNotebook } from '../../store/notebooks';

const CreateNotebookForm = ({ hideForm }) => {
  const currentUserId = useSelector((state) => state.session.user.id)
  const dispatch = useDispatch();
  const history = useHistory();
  const [userId, setUserId] = useState(currentUserId)
  const [title, setTitle] = useState('');

  const updateTitle = (e) => setTitle(e.target.value);

  useEffect(() => {
    dispatch(getAllRaves());
  }, [dispatch]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      userId,
      title,
      image,
      description,
      address,
      city,
      state,
      zipCode,
      date
    };

    let createdRave;
    createdRave = await dispatch(createRave(payload))
    if (createdRave) {
      history.push(`/`);
    }
  };

  const handleCancelClick = (e) => {
    e.preventDefault();
  };

  return (
    <section className="new-form-holder centered middled">
      <form className="create-rave-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          required
          value={title}
          onChange={updateTitle} />

        <button type="submit">Post New Rave </button>
        <button type="button" onClick={handleCancelClick}>Cancel</button>
      </form>
    </section>
  );
}


export default CreateNotebookForm;
